import { Component, inject, OnDestroy, OnInit } from '@angular/core';
import {
  NonNullableFormBuilder,
  ReactiveFormsModule,
  Validators
} from '@angular/forms';

import { NzButtonModule } from 'ng-zorro-antd/button';
import { NzCheckboxModule } from 'ng-zorro-antd/checkbox';
import { NzFormModule } from 'ng-zorro-antd/form';
import { NzIconModule } from 'ng-zorro-antd/icon';
import { NzInputModule } from 'ng-zorro-antd/input';
import { NzSelectModule } from 'ng-zorro-antd/select';
import { MoviesManagementService } from '../../services/movies.management.service';
import { NzCardModule } from 'ng-zorro-antd/card';
import { MoviePublishRequest, MoviePublishResponse, MovieSearchResponse } from '../../types/management.movies.types';
import { NzImageModule } from 'ng-zorro-antd/image';
import { NzDatePickerModule } from 'ng-zorro-antd/date-picker';
import { GlobalAlertService } from '../../../shared/components/alert/global.alert.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-publish',
  imports: [ReactiveFormsModule,
    NzButtonModule,
    NzCheckboxModule,
    NzFormModule,
    NzIconModule,
    NzInputModule,
    NzSelectModule,
    NzCardModule,
    NzImageModule,
    NzDatePickerModule
  ],
  templateUrl: './publish.component.html',
  styleUrl: './publish.component.css'
})
export class PublishComponent implements OnInit, OnDestroy {
  fallback =
    'data:image/png;base64,';
  private fb = inject(NonNullableFormBuilder);
  movieSearchInfo: MovieSearchResponse | null = null;
  validateForm = this.fb.group({
    name: this.fb.control('', [Validators.required]),
    description: this.fb.control('', [Validators.required]),
    genre: this.fb.control('', [Validators.required]),
    releaseDate: this.fb.control<Date | null>(null, [Validators.required]),
  });
  searchingMovie = false;

  constructor(private movieService: MoviesManagementService,
    private alert : GlobalAlertService,
    private router: Router
  ) { }

  ngOnInit(): void {
  }

  ngOnDestroy(): void {
  }

  searchMovie(): void {
    const movieName = this.validateForm.get('name')?.value;
    if (movieName) {
      this.searchingMovie = true;
      this.movieService.searchMovie(movieName).subscribe({
        next: (response) => {
          console.log('Movie Search results:', response);

          this.movieSearchInfo = response;

          this.validateForm.patchValue({
            name: response.title,
            description: response.plot,
            genre: response.genre,
            releaseDate: response.released ? new Date(response.released) : null
          });

          this.searchingMovie = false;
        },
        error: (error) => {
          if (error.status === 404) {
            this.alert.show('warning', 'Movie not found. Please check the name and try again.');
          }
          console.error('Error searching for movie:', error);
          this.searchingMovie = false;
        }
      });
    } else {
      console.warn('Movie name is required for search');
    }
  }

  submitForm(): void {
    if (this.validateForm.valid) {
      console.log('submit', this.validateForm.value);

      const publishMovieRequest: MoviePublishRequest = {
        name: this.validateForm.get('name')!.value,
        description: this.validateForm.get('description')!.value,
        genre: this.validateForm.get('genre')!.value,
        releaseDate: this.validateForm.get('releaseDate')!.value!,
        posterUri: this.movieSearchInfo!.poster
      };

      this.movieService.publishMovie(publishMovieRequest).subscribe({
        next: (response: MoviePublishResponse) => {
          console.log('Movie published successfully:', response);
          // Optionally, reset the form or show a success message
          this.validateForm.reset();
          this.movieSearchInfo = null;

          this.alert.show('success', 'Movie published successfully!');

          this.router.navigate(['']);
        },
        error: (error) => {
          console.error('Error publishing movie:', error);
          // Optionally, show an error message to the user
        }
      });

    } else {
      Object.values(this.validateForm.controls).forEach(control => {
        if (control.invalid) {
          control.markAsDirty();
          control.updateValueAndValidity({ onlySelf: true });
        }
      });
    }
  }
}
